    public static int maxDistToClosest(int[] seats) {
        int start = 0, end = 0, maxZero = 0, temp = 0, type = -1;
        boolean find = false;
        for (int i = 0; i < seats.length; i++) {
            if (find) {
                if (seats[i] == 1) {
                    find = false;
                    if (type == 1) {
                        if (maxZero < (i - temp + 1) / 2) {
                            start = temp;
                            end = i - 1;
                            maxZero = (end - start + 1 + 1) / 2;
                        }
                    } else {
                        start = 0;
                        end = i - 1;
                        maxZero = i;
                    }
                } else {
                    if (i == seats.length - 1) {
                        if (maxZero < seats.length - temp) {
                            start = temp;
                            end = i;
                            maxZero = seats.length - start;
                        }
                    }
                }
            } else {
                if (seats[i] == 0) {
                    if (i == 0) {
                        type = 0;
                    } else {
                        type = 1;
                    }

                    find = true;
                    temp = i;
                }
            }
        }

        if (start != 0) {
            if (seats[0] == 0) {
                for (int i = 0; i < seats.length; i++) {
                    if (seats[i] == 1) {
                        if (i + 1 > maxZero) {
                            end = i - 1;
                            maxZero = i;
                        }
                        break;
                    }
                }
            }
        }

        if (end != seats.length - 1) {
            if (seats[seats.length - 1] == 0) {
                for (int i = seats.length - 1; i > 0; i--) {
                    if (seats[i] == 1) {
                        if (seats.length - i - 1 > maxZero) {
                            maxZero = seats.length - i - 1;
                        }
                        break;
                    }
                }
            }
        }
        return maxZero;
    }
