class RepeatedSubstringPattern {

    public static void main(String[] args) {
        //获取开始时间
        long startTime = System.currentTimeMillis();


//        System.out.println(repeatedSubstringPattern("bb"));
//        System.out.println(repeatedSubstringPattern("ab"));
        System.out.println(repeatedSubstringPattern2("abab"));
//        System.out.println(repeatedSubstringPattern("aba"));
//        System.out.println(repeatedSubstringPattern("abcabcabcabc"));
        System.out.println(repeatedSubstringPattern2(""));
        //获取结束时间
        long endTime = System.currentTimeMillis();
        System.out.println("程序运行时间： " + (endTime - startTime) + "ms");
    }

    //超时
    public static boolean repeatedSubstringPattern(String s) {
        int length = 0;
        while (length < s.length() / 2) {
            StringBuilder sb = new StringBuilder().append(s, length+1, s.length()).append(s, 0, length + 1);
            if(sb.toString().equals(s)){
                return true;
            }
            length++;
        }
        return false;
    }


    public static boolean repeatedSubstringPattern2(String s) {
        int start = 0;
        while (start < s.length() / 2){
            if(s.charAt(start) == s.charAt(s.length() -1)){
                if(s.split(s.substring(0,start+1)).length == 0){
                    return true;
                }
            }
            start ++;
        }

        return false;
    }

}
